mapper.select:
SELECT * FROM org_group_user


        ${whereSql}




    ORDER BY ${orderBySql}


    ORDER BY id_ DESC

mapper.update:
		UPDATE org_group_user SET
        rel_id_=#{relId,jdbcType=VARCHAR}
		WHERE
		id_=#{id}

mapper.delete:
DELETE FROM org_group_user 
WHERE
user_id_=#{userId} and group_id_ =#{groupId}

mapper.select:
SELECT u.id_ "userId",u.mobile_ "mobile",u.weixin_ "weixin",u.email_ "email", u.fullname_ "fullName",u.account_ "account", u.create_time_ "createTime",u.sex_ "sex",u.status_ "status",GroupUser.id_ "groupUserId", org.id_ "groupId", org.name_ "orgName" ,org.code_ "orgCode", GroupUser.is_master_ "isMaster",rel.rel_name_ "relName" FROM org_user u join org_group_user GroupUser on u.id_=GroupUser.user_id_ join org_group org on org.id_=GroupUser.group_id_ LEFT JOIN org_group_rel rel ON GroupUser.rel_id_=rel.id_
${whereSql}
ORDER BY ${orderBySql}
ORDER BY u.create_time_ DESC

SELECT * FROM org_group_user
WHERE  group_id_=#{groupId}  and user_id_=#{userId}

    and rel_id_=#{relId}


    and rel_id_ is null
